import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import session from "express-session";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

// Session configuration
app.use(session({
  secret: process.env.SESSION_SECRET || 'fallback-secret-for-dev',
  resave: false,
  saveUninitialized: false,
  cookie: {
    secure: false, // Set to true in production with HTTPS
    httpOnly: true,
    maxAge: 24 * 60 * 60 * 1000 // 24 hours
  }
}));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import bcrypt from "bcryptjs";
import { insertDoctorSchema, insertPatientSchema, insertPrescriptionSchema } from "@shared/schema";
import { z } from "zod";

declare module "express-session" {
  interface SessionData {
    doctorId: string;
  }
}

export async function registerRoutes(app: Express): Promise<Server> {
  // Doctor registration
  app.post("/api/auth/register", async (req, res) => {
    try {
      const doctorData = insertDoctorSchema.parse(req.body);

      // Check if username or medical license ID already exists
      const existingByUsername = await storage.getDoctorByUsername(doctorData.username);
      if (existingByUsername) {
        return res.status(400).json({ message: "Username already exists" });
      }

      const existingByLicense = await storage.getDoctorByMedicalLicense(doctorData.medicalLicenseId);
      if (existingByLicense) {
        return res.status(400).json({ message: "Medical license ID already registered" });
      }

      // Hash password
      const hashedPassword = await bcrypt.hash(doctorData.password, 10);

      const doctor = await storage.createDoctor({
        ...doctorData,
        password: hashedPassword,
      });

      if (req.session) {
        req.session.doctorId = doctor.id;
      }

      res.json({ 
        message: "Registration successful", 
        doctor: { 
          id: doctor.id, 
          username: doctor.username,
          firstName: doctor.firstName,
          lastName: doctor.lastName,
          specialization: doctor.specialization 
        } 
      });
    } catch (error) {
      console.error("Registration error:", error);
      res.status(400).json({ message: "Invalid registration data" });
    }
  });

  // Doctor login
  app.post("/api/auth/login", async (req, res) => {
    try {
      const { identifier, password } = req.body;

      if (!identifier || !password) {
        return res.status(400).json({ message: "Username/License ID and password are required" });
      }

      const doctor = await storage.getDoctorByUsernameOrLicense(identifier);
      if (!doctor) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      const isValidPassword = await bcrypt.compare(password, doctor.password);
      if (!isValidPassword) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      if (req.session) {
        req.session.doctorId = doctor.id;
      }

      res.json({ 
        message: "Login successful", 
        doctor: { 
          id: doctor.id, 
          username: doctor.username,
          firstName: doctor.firstName,
          lastName: doctor.lastName,
          specialization: doctor.specialization 
        } 
      });
    } catch (error) {
      console.error("Login error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Doctor logout
  app.post("/api/auth/logout", (req, res) => {
    req.session.destroy((err: any) => {
      if (err) {
        return res.status(500).json({ message: "Could not log out" });
      }
      res.json({ message: "Logout successful" });
    });
  });

  // Get current doctor
  app.get("/api/auth/me", async (req, res) => {
    if (!req.session?.doctorId) {
      return res.status(401).json({ message: "Not authenticated" });
    }

    try {
      const doctor = await storage.getDoctor(req.session.doctorId);
      if (!doctor) {
        return res.status(404).json({ message: "Doctor not found" });
      }

      res.json({ 
        doctor: { 
          id: doctor.id, 
          username: doctor.username,
          firstName: doctor.firstName,
          lastName: doctor.lastName,
          specialization: doctor.specialization 
        } 
      });
    } catch (error) {
      console.error("Get current doctor error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Middleware to check authentication
  const requireAuth = (req: any, res: any, next: any) => {
    if (!req.session?.doctorId) {
      return res.status(401).json({ message: "Authentication required" });
    }
    next();
  };

  // Patient routes
  app.get("/api/patients", requireAuth, async (req, res) => {
    try {
      const { search } = req.query;
      let patients;

      if (search && typeof search === 'string') {
        patients = await storage.searchPatients(req.session.doctorId, search);
      } else {
        patients = await storage.getPatientsByDoctor(req.session.doctorId);
      }

      res.json(patients);
    } catch (error) {
      console.error("Get patients error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.get("/api/patients/:id", requireAuth, async (req, res) => {
    try {
      const patient = await storage.getPatient(req.params.id);
      if (!patient || patient.doctorId !== req.session.doctorId) {
        return res.status(404).json({ message: "Patient not found" });
      }
      res.json(patient);
    } catch (error) {
      console.error("Get patient error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.post("/api/patients", requireAuth, async (req, res) => {
    try {
      const patientData = insertPatientSchema.parse({
        ...req.body,
        doctorId: req.session.doctorId,
      });

      const patient = await storage.createPatient(patientData);
      res.json(patient);
    } catch (error) {
      console.error("Create patient error:", error);
      res.status(400).json({ message: "Invalid patient data" });
    }
  });

  app.put("/api/patients/:id", requireAuth, async (req, res) => {
    try {
      const patient = await storage.getPatient(req.params.id);
      if (!patient || patient.doctorId !== req.session.doctorId) {
        return res.status(404).json({ message: "Patient not found" });
      }

      const updateData = insertPatientSchema.partial().parse(req.body);
      const updatedPatient = await storage.updatePatient(req.params.id, updateData);
      res.json(updatedPatient);
    } catch (error) {
      console.error("Update patient error:", error);
      res.status(400).json({ message: "Invalid patient data" });
    }
  });

  // Prescription routes
  app.get("/api/prescriptions", requireAuth, async (req, res) => {
    try {
      const prescriptions = await storage.getPrescriptionsByDoctor(req.session.doctorId);
      res.json(prescriptions);
    } catch (error) {
      console.error("Get prescriptions error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.get("/api/patients/:patientId/prescriptions", requireAuth, async (req, res) => {
    try {
      const patient = await storage.getPatient(req.params.patientId);
      if (!patient || patient.doctorId !== req.session.doctorId) {
        return res.status(404).json({ message: "Patient not found" });
      }

      const prescriptions = await storage.getPrescriptionsByPatient(req.params.patientId);
      res.json(prescriptions);
    } catch (error) {
      console.error("Get patient prescriptions error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.post("/api/prescriptions", requireAuth, async (req, res) => {
    try {
      const prescriptionData = insertPrescriptionSchema.parse({
        ...req.body,
        doctorId: req.session.doctorId,
      });

      const prescription = await storage.createPrescription(prescriptionData);
      res.json(prescription);
    } catch (error) {
      console.error("Create prescription error:", error);
      res.status(400).json({ message: "Invalid prescription data" });
    }
  });

  // Dashboard stats
  app.get("/api/dashboard/stats", requireAuth, async (req, res) => {
    try {
      const patients = await storage.getPatientsByDoctor(req.session.doctorId);
      const prescriptions = await storage.getPrescriptionsByDoctor(req.session.doctorId);

      const today = new Date().toISOString().split('T')[0];
      const todayPrescriptions = prescriptions.filter(p => 
        p.createdAt && p.createdAt.toISOString().split('T')[0] === today
      );

      res.json({
        todayPatients: patients.filter(p => 
          p.createdAt && p.createdAt.toISOString().split('T')[0] === today
        ).length,
        prescriptions: todayPrescriptions.length,
        pendingReviews: 0, // Placeholder for future functionality
        totalPatients: patients.length,
      });
    } catch (error) {
      console.error("Get dashboard stats error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { doctors, patients, prescriptions, type Doctor, type InsertDoctor, type Patient, type InsertPatient, type Prescription, type InsertPrescription } from "@shared/schema";
import { db } from "./db";
import { eq, or, ilike, desc } from "drizzle-orm";

export interface IStorage {
  // Doctor methods
  getDoctor(id: string): Promise<Doctor | undefined>;
  getDoctorByUsername(username: string): Promise<Doctor | undefined>;
  getDoctorByMedicalLicense(licenseId: string): Promise<Doctor | undefined>;
  getDoctorByUsernameOrLicense(identifier: string): Promise<Doctor | undefined>;
  createDoctor(doctor: InsertDoctor): Promise<Doctor>;

  // Patient methods
  getPatient(id: string): Promise<Patient | undefined>;
  getPatientsByDoctor(doctorId: string): Promise<Patient[]>;
  createPatient(patient: InsertPatient): Promise<Patient>;
  updatePatient(id: string, patient: Partial<InsertPatient>): Promise<Patient>;
  searchPatients(doctorId: string, query: string): Promise<Patient[]>;

  // Prescription methods
  getPrescription(id: string): Promise<Prescription | undefined>;
  getPrescriptionsByPatient(patientId: string): Promise<Prescription[]>;
  getPrescriptionsByDoctor(doctorId: string): Promise<Prescription[]>;
  createPrescription(prescription: InsertPrescription): Promise<Prescription>;

  // For backwards compatibility
  getUser(id: string): Promise<Doctor | undefined>;
  getUserByUsername(username: string): Promise<Doctor | undefined>;
  createUser(user: InsertDoctor): Promise<Doctor>;
}

export class DatabaseStorage implements IStorage {
  // Doctor methods
  async getDoctor(id: string): Promise<Doctor | undefined> {
    const [doctor] = await db.select().from(doctors).where(eq(doctors.id, id));
    return doctor || undefined;
  }

  async getDoctorByUsername(username: string): Promise<Doctor | undefined> {
    const [doctor] = await db.select().from(doctors).where(eq(doctors.username, username));
    return doctor || undefined;
  }

  async getDoctorByMedicalLicense(licenseId: string): Promise<Doctor | undefined> {
    const [doctor] = await db.select().from(doctors).where(eq(doctors.medicalLicenseId, licenseId));
    return doctor || undefined;
  }

  async getDoctorByUsernameOrLicense(identifier: string): Promise<Doctor | undefined> {
    const [doctor] = await db.select().from(doctors).where(
      or(
        eq(doctors.username, identifier),
        eq(doctors.medicalLicenseId, identifier)
      )
    );
    return doctor || undefined;
  }

  async createDoctor(insertDoctor: InsertDoctor): Promise<Doctor> {
    const [doctor] = await db
      .insert(doctors)
      .values(insertDoctor)
      .returning();
    return doctor;
  }

  // Patient methods
  async getPatient(id: string): Promise<Patient | undefined> {
    const [patient] = await db.select().from(patients).where(eq(patients.id, id));
    return patient || undefined;
  }

  async getPatientsByDoctor(doctorId: string): Promise<Patient[]> {
    return await db.select().from(patients).where(eq(patients.doctorId, doctorId)).orderBy(desc(patients.createdAt));
  }

  async createPatient(insertPatient: InsertPatient): Promise<Patient> {
    const [patient] = await db
      .insert(patients)
      .values(insertPatient)
      .returning();
    return patient;
  }

  async updatePatient(id: string, updateData: Partial<InsertPatient>): Promise<Patient> {
    const [patient] = await db
      .update(patients)
      .set(updateData)
      .where(eq(patients.id, id))
      .returning();
    return patient;
  }

  async searchPatients(doctorId: string, query: string): Promise<Patient[]> {
    return await db.select().from(patients).where(
      or(
        ilike(patients.firstName, `%${query}%`),
        ilike(patients.lastName, `%${query}%`),
        ilike(patients.phone, `%${query}%`),
        ilike(patients.email, `%${query}%`)
      )
    ).orderBy(desc(patients.createdAt));
  }

  // Prescription methods
  async getPrescription(id: string): Promise<Prescription | undefined> {
    const [prescription] = await db.select().from(prescriptions).where(eq(prescriptions.id, id));
    return prescription || undefined;
  }

  async getPrescriptionsByPatient(patientId: string): Promise<Prescription[]> {
    return await db.select().from(prescriptions).where(eq(prescriptions.patientId, patientId)).orderBy(desc(prescriptions.createdAt));
  }

  async getPrescriptionsByDoctor(doctorId: string): Promise<Prescription[]> {
    return await db.select().from(prescriptions).where(eq(prescriptions.doctorId, doctorId)).orderBy(desc(prescriptions.createdAt));
  }

  async createPrescription(insertPrescription: InsertPrescription): Promise<Prescription> {
    const [prescription] = await db
      .insert(prescriptions)
      .values({
        ...insertPrescription,
        medicines: JSON.stringify(insertPrescription.medicines)
      } as any)
      .returning();
    return prescription;
  }

  // For backwards compatibility
  async getUser(id: string): Promise<Doctor | undefined> {
    return this.getDoctor(id);
  }

  async getUserByUsername(username: string): Promise<Doctor | undefined> {
    return this.getDoctorByUsername(username);
  }

  async createUser(user: InsertDoctor): Promise<Doctor> {
    return this.createDoctor(user);
  }
}

export const storage = new DatabaseStorage();

