client/src/utils/pdf-generator.ts.content:
interface PrescriptionData {
  doctor: {
    firstName: string;
    lastName: string;
    specialization?: string;
    medicalLicenseId: string;
  };
  patient: {
    firstName: string;
    lastName: string;
    phone: string;
    dateOfBirth: string;
  };
  medicines: Array<{
    name: string;
    dosage: string;
    frequency: string;
    duration: string;
    instructions?: string;
  }>;
  additionalNotes: string;
  date: string;
}

export const generatePrescriptionPDF = (data: PrescriptionData) => {
  // Create a new window for the prescription
  const printWindow = window.open('', '_blank', 'width=800,height=600');

  if (!printWindow) {
    alert('Please allow popups to generate the prescription PDF');
    return;
  }

  const calculateAge = (dateOfBirth: string) => {
    const today = new Date();
    const birthDate = new Date(dateOfBirth);
    let age = today.getFullYear() - birthDate.getFullYear();
    const monthDiff = today.getMonth() - birthDate.getMonth();
    if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthDate.getDate())) {
      age--;
    }
    return age;
  };

  const prescriptionHTML = `
    <!DOCTYPE html>
    <html lang="en">
    <head>
        <meta charset="UTF-8">
        <meta name="viewport" content="width=device-width, initial-scale=1.0">
        <title>Medical Prescription - Dr. ${data.doctor.firstName} ${data.doctor.lastName}</title>
        <style>
            body {
                font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
                max-width: 800px;
                margin: 0 auto;
                padding: 40px 20px;
                line-height: 1.6;
                color: #333;
                background: white;
            }

            .header {
                border-bottom: 3px solid #2563EB;
                padding-bottom: 20px;
                margin-bottom: 30px;
                text-align: center;
            }

            .doctor-info {
                margin-bottom: 15px;
            }

            .doctor-name {
                font-size: 24px;
                font-weight: bold;
                color: #2563EB;
                margin: 0;
            }

            .doctor-specialty {
                font-size: 16px;
                color: #666;
                margin: 5px 0;
            }

            .license-info {
                font-size: 14px;
                color: #666;
            }

            .patient-section {
                background: #f8fafc;
                border: 1px solid #e2e8f0;
                border-radius: 8px;
                padding: 20px;
                margin-bottom: 30px;
            }

            .patient-info {
                display: grid;
                grid-template-columns: 1fr 1fr;
                gap: 15px;
            }

            .info-group {
                margin-bottom: 10px;
            }

            .info-label {
                font-weight: 600;
                color: #374151;
                margin-bottom: 2px;
            }

            .info-value {
                color: #6b7280;
            }

            .prescription-section {
                margin-bottom: 30px;
            }

            .section-title {
                font-size: 18px;
                font-weight: bold;
                color: #2563EB;
                border-bottom: 2px solid #e5e7eb;
                padding-bottom: 5px;
                margin-bottom: 20px;
            }

            .medicine-item {
                border: 1px solid #e5e7eb;
                border-radius: 6px;
                margin-bottom: 15px;
                padding: 15px;
                background: white;
            }

            .medicine-name {
                font-size: 16px;
                font-weight: bold;
                color: #1f2937;
                margin-bottom: 8px;
            }

            .medicine-details {
                display: grid;
                grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
                gap: 10px;
                margin-bottom: 8px;
            }

            .medicine-detail {
                font-size: 14px;
            }

            .detail-label {
                font-weight: 600;
                color: #4b5563;
            }

            .detail-value {
                color: #6b7280;
            }

            .medicine-instructions {
                font-size: 14px;
                color: #059669;
                font-style: italic;
                margin-top: 5px;
            }

            .notes-section {
                background: #fef3c7;
                border: 1px solid #fbbf24;
                border-radius: 6px;
                padding: 15px;
                margin-bottom: 30px;
            }

            .footer {
                border-top: 2px solid #e5e7eb;
                padding-top: 20px;
                margin-top: 40px;
                display: flex;
                justify-content: space-between;
                align-items: center;
            }

            .signature-section {
                text-align: right;
            }

            .signature-line {
                border-bottom: 1px solid #6b7280;
                width: 200px;
                height: 40px;
                margin-bottom: 5px;
                margin-left: auto;
            }

            .date-issued {
                font-size: 14px;
                color: #6b7280;
            }

            @media print {
                body {
                    padding: 20px;
                }

                .no-print {
                    display: none;
                }
            }
        </style>
    </head>
    <body>
        <div class="header">
            <div class="doctor-info">
                <h1 class="doctor-name">Dr. ${data.doctor.firstName} ${data.doctor.lastName}</h1>
                <p class="doctor-specialty">${data.doctor.specialization || 'MD'}</p>
                <p class="license-info">Medical License: ${data.doctor.medicalLicenseId}</p>
            </div>
        </div>

        <div class="patient-section">
            <h2 class="section-title">Patient Information</h2>
            <div class="patient-info">
                <div class="info-group">
                    <div class="info-label">Patient Name:</div>
                    <div class="info-value">${data.patient.firstName} ${data.patient.lastName}</div>
                </div>
                <div class="info-group">
                    <div class="info-label">Age:</div>
                    <div class="info-value">${calculateAge(data.patient.dateOfBirth)} years</div>
                </div>
                <div class="info-group">
                    <div class="info-label">Phone:</div>
                    <div class="info-value">${data.patient.phone}</div>
                </div>
                <div class="info-group">
                    <div class="info-label">Date:</div>
                    <div class="info-value">${data.date}</div>
                </div>
            </div>
        </div>

        <div class="prescription-section">
            <h2 class="section-title">â„ž Prescription</h2>
            ${data.medicines.map((medicine, index) => `
                <div class="medicine-item">
                    <div class="medicine-name">${index + 1}. ${medicine.name} ${medicine.dosage}</div>
                    <div class="medicine-details">
                        <div class="medicine-detail">
                            <span class="detail-label">Frequency:</span>
                            <span class="detail-value">${medicine.frequency}</span>
                        </div>
                        <div class="medicine-detail">
                            <span class="detail-label">Duration:</span>
                            <span class="detail-value">${medicine.duration}</span>
                        </div>
                    </div>
                    ${medicine.instructions ? `<div class="medicine-instructions">Instructions: ${medicine.instructions}</div>` : ''}
                </div>
            `).join('')}
        </div>

        ${data.additionalNotes ? `
            <div class="notes-section">
                <h3 class="section-title" style="margin-bottom: 10px; font-size: 16px;">Additional Notes:</h3>
                <p style="margin: 0;">${data.additionalNotes}</p>
            </div>
        ` : ''}

        <div class="footer">
            <div class="date-issued">
                Date Issued: ${data.date}
            </div>
            <div class="signature-section">
                <div class="signature-line"></div>
                <div style="font-size: 14px; color: #6b7280;">
                    Dr. ${data.doctor.firstName} ${data.doctor.lastName}
                </div>
            </div>
        </div>

        <div class="no-print" style="text-align: center; margin-top: 30px;">
            <button onclick="window.print()" style="
                background: #2563EB;
                color: white;
                border: none;
                padding: 10px 20px;
                border-radius: 6px;
                font-size: 16px;
                cursor: pointer;
                margin-right: 10px;
            ">Print Prescription</button>
            <button onclick="window.close()" style="
                background: #6b7280;
                color: white;
                border: none;
                padding: 10px 20px;
                border-radius: 6px;
                font-size: 16px;
                cursor: pointer;
            ">Close</button>
        </div>
    </body>
    </html>
  `;

  printWindow.document.write(prescriptionHTML);
  printWindow.document.close();
};

client/src/utils/prescription-parser.ts.content:
interface ParsedMedicine {
  name: string;
  dosage: string;
  frequency: string;
  duration: string;
  instructions: string;
}

export const parsePrescriptionText = (text: string): ParsedMedicine[] => {
  const medicines: ParsedMedicine[] = [];
  const cleanText = text.toLowerCase().trim();

  // Common medicine name patterns
  const medicinePatterns = [
    /(\w*cillin|ibuprofen|acetaminophen|aspirin|metformin|lisinopril|amlodipine|atorvastatin|omeprazole|levothyroxine|paracetamol|diclofenac|naproxen|warfarin|insulin|furosemide|doxycycline|tramadol|codeine|morphine|gabapentin|sertraline|fluoxetine|lorazepam|diazepam)\s*(\d+\s*(?:mg|mcg|g|ml|units?))?/gi,
    /(tablet|capsule|syrup|injection|cream|ointment|drops)\s+of\s+(\w+)/gi,
    /(\w+)\s+(\d+\s*(?:mg|mcg|g|ml|units?))/gi,
  ];

  // Dosage patterns
  const dosagePatterns = [
    /(\d+\s*(?:mg|mcg|g|ml|units?))/gi,
    /(\d+\s*milligrams?|\d+\s*micrograms?|\d+\s*grams?)/gi,
  ];

  // Frequency patterns
  const frequencyPatterns = [
    /(once|twice|three times?|four times?|1|2|3|4)\s*(?:times?\s*)?(?:daily|per day|a day)/gi,
    /(every\s+\d+\s+hours?)/gi,
    /(morning|evening|night|bedtime)/gi,
    /(as needed|when needed|prn)/gi,
  ];

  // Duration patterns
  const durationPatterns = [
    /(?:for\s+)?(\d+\s*(?:days?|weeks?|months?))/gi,
    /(until\s+\w+|until\s+symptoms\s+improve)/gi,
  ];

  // Instructions patterns
  const instructionPatterns = [
    /((?:take|apply|use)\s+(?:after|before|with|without)\s+(?:meals?|food|eating))/gi,
    /(on empty stomach|with plenty of water)/gi,
  ];

  // Split text into sentences for better parsing
  const sentences = cleanText.split(/[.!?]+/).filter(s => s.trim().length > 0);

  sentences.forEach(sentence => {
    const trimmedSentence = sentence.trim();

    // Try to find medicine names
    let medicineName = '';
    let dosage = '';
    let frequency = '';
    let duration = '';
    let instructions = '';

    // Extract medicine name
    for (const pattern of medicinePatterns) {
      const matches = pattern.exec(trimmedSentence);
      if (matches) {
        medicineName = matches[1] || matches[3] || matches[0];
        if (matches[2]) dosage = matches[2].trim();
        pattern.lastIndex = 0; // Reset regex
        break;
      }
    }

    // If no medicine found with patterns, try to identify common words
    if (!medicineName) {
      const words = trimmedSentence.split(/\s+/);
      for (const word of words) {
        if (word.length > 3 && /^[a-z]+$/.test(word) && 
            (word.includes('cillin') || word.includes('phen') || word.includes('stat') || 
             word.includes('zole') || word.includes('pine') || word.includes('pril'))) {
          medicineName = word;
          break;
        }
      }
    }

    if (medicineName) {
      // Extract dosage if not already found
      if (!dosage) {
        const dosageMatch = dosagePatterns[0].exec(trimmedSentence);
        if (dosageMatch) dosage = dosageMatch[1];
        dosagePatterns[0].lastIndex = 0;
      }

      // Extract frequency
      const frequencyMatch = frequencyPatterns[0].exec(trimmedSentence);
      if (frequencyMatch) {
        frequency = frequencyMatch[0];
      } else {
        // Check for numbers that might indicate frequency
        const numberMatch = /(\d+)\s*times?/gi.exec(trimmedSentence);
        if (numberMatch) {
          const num = parseInt(numberMatch[1]);
          if (num === 1) frequency = 'Once daily';
          else if (num === 2) frequency = 'Twice daily';
          else if (num === 3) frequency = 'Three times daily';
          else if (num === 4) frequency = 'Four times daily';
          else frequency = `${num} times daily`;
        }
      }
      frequencyPatterns[0].lastIndex = 0;

      // Extract duration
      const durationMatch = durationPatterns[0].exec(trimmedSentence);
      if (durationMatch) duration = durationMatch[1];
      durationPatterns[0].lastIndex = 0;

      // Extract instructions
      const instructionMatch = instructionPatterns[0].exec(trimmedSentence);
      if (instructionMatch) instructions = instructionMatch[1];
      instructionPatterns[0].lastIndex = 0;

      // Set defaults if not found
      if (!frequency) frequency = 'As directed';
      if (!duration) duration = 'As needed';
      if (!dosage) dosage = 'As prescribed';

      medicines.push({
        name: capitalizeFirst(medicineName),
        dosage: dosage,
        frequency: capitalizeFirst(frequency),
        duration: duration,
        instructions: capitalizeFirst(instructions),
      });
    }
  });

  // If no medicines found, return empty array to not interfere with form
  return medicines.length > 0 ? medicines : [];
};

const capitalizeFirst = (str: string): string => {
  if (!str) return '';
  return str.charAt(0).toUpperCase() + str.slice(1).toLowerCase();
};

client/src/utils/speech-recognition.ts.content:
let recognition: any | null = null;
let isListening = false;

export const startSpeechRecognition = (onResult: (transcript: string) => void): Promise<void> => {
  return new Promise((resolve, reject) => {
    if (!('webkitSpeechRecognition' in window) && !('SpeechRecognition' in window)) {
      reject(new Error('Speech recognition not supported in this browser'));
      return;
    }

    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    recognition = new SpeechRecognition();

    recognition.continuous = true;
    recognition.interimResults = true;
    recognition.lang = 'en-US';

    let finalTranscript = '';

    recognition.onresult = (event: any) => {
      let interimTranscript = '';

      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcript = event.results[i][0].transcript;
        if (event.results[i].isFinal) {
          finalTranscript += transcript + ' ';
        } else {
          interimTranscript += transcript;
        }
      }

      onResult(finalTranscript + interimTranscript);
    };

    recognition.onerror = (event: any) => {
      console.error('Speech recognition error:', event.error);
      reject(new Error(`Speech recognition error: ${event.error}`));
    };

    recognition.onstart = () => {
      isListening = true;
      resolve();
    };

    recognition.onend = () => {
      isListening = false;
    };

    try {
      recognition.start();
    } catch (error) {
      reject(error);
    }
  });
};

export const stopSpeechRecognition = () => {
  if (recognition && isListening) {
    recognition.stop();
    recognition = null;
    isListening = false;
  }
};

export const isSpeechRecognitionSupported = (): boolean => {
  return 'webkitSpeechRecognition' in window || 'SpeechRecognition' in window;
};

// Extend the Window interface to include speech recognition
declare global {
  interface Window {
    SpeechRecognition: any;
    webkitSpeechRecognition: any;
  }
}

